public class zad3{    public static void createNewArray(){        int[] numberArrayOne = {1,2,3,4,5,1};        int[] numberArrayTwo = {4,10,2,6,20};        int[] result = new int[6];        int tempOne=0;        int tempTwo = 0;        int counter =0;        for (int i = 0; i < numberArrayOne.length; i++) {            tempOne = numberArrayOne[i];            for (int j = 0; j < numberArrayTwo.length; j++)            {                tempTwo= numberArrayTwo[j];                if(tempOne ==tempTwo){                    counter++;                    result[counter] = tempOne;                }            }        }        System.out.print("numberArrayOne: " + "{1,2,3,4,5,1};");        System.out.println();        System.out.print("numberArrayTwo: " + "{4,10,2,6,20};");        System.out.println();        System.out.print("Result: {");        for(int finalRes:result){        if(finalRes!=0) {        System.out.print(finalRes + ",");        }        }        System.out.print("}");        System.out.println();    }    public static void main(String[] args)    {    createNewArray();    }}